package _9;
public class ExponentFuncion {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("e的平方值："+Math.exp(2));	//取2的平方值
		//取以e为底2的对数
		System.out.println("以e为底的对数值："+Math.log(2));
		//取以10为底2的对数
		System.out.println("以10为底的对数值："+Math.log10(2));
		System.out.println("4的平方根值："+Math.sqrt(4));	//取4的平方根
		System.out.println("8的立方根值："+Math.cbrt(8));	//取8的立方根
		System.out.println("2的2次方值："+Math.pow(2,2));	//取2的2次方
	}
}
